    private void cclabelMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cclabelMouseClicked

        SqlUsuarios modSql = new SqlUsuarios();
        usuarios mod = new usuarios();

        String passText = new String(pass.getPassword());
        String passcon = new String(conpass.getPassword());

        if (username.getText().equals("") || pass.equals("") || conpass.equals("") || String.valueOf(pass.getPassword()).equals("********") || String.valueOf(conpass.getPassword()).equals("********") || username.getText().equals("Ingrese nombre de usuario")) {
            JOptionPane.showMessageDialog(null, "Por favor, llene todos los campos");
        } else {

            if (passText.equals(passcon)) {

                if (modSql.ValidacionUsuario(username.getText()) == 0) {

                

                String nuevoPass = hash.sha1(passText);

                mod.setUsuario(username.getText());
                mod.setPassword(nuevoPass);
                mod.setId_tipo(1);

                if (modSql.registrar(mod)) {
                    JOptionPane.showMessageDialog(null, "Se ha registrado con exito");
                    limpiar();
                    new login().setVisible(true);
                    dispose();
                } else {
                    JOptionPane.showMessageDialog(null, "No se pudo registrar, quiza otra vez");
                }
                } else {
                JOptionPane.showMessageDialog(null, "El nombre de usuario ya esta siendo usado por otro aventurero");
                }
        }else {
            JOptionPane.showMessageDialog(null, "Las contraseñas no coinciden");
        }
    }
        
    }//GEN-LAST:event_cclabelMouseClicked

    private void limpiar(){
        pass.setText("");
        conpass.setText("");
        username.setText("");
    }
