    private void usernameMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_usernameMousePressed
        if(username.getText().equals("Ingrese nombre de usuario")){
         username.setText("");
        }
        
        if(String.valueOf(pass.getPassword()).isEmpty()){
         pass.setText("********");
        }
        
        if(String.valueOf(conpass.getPassword()).isEmpty()){
         conpass.setText("********");
        }
    }//GEN-LAST:event_usernameMousePressed

    private void passMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_passMousePressed
        if(String.valueOf(pass.getPassword()).equals("********")){
         pass.setText("");
        }
        
        if(username.getText().isEmpty()){
         username.setText("Ingrese nombre de usuario");
        }
        
        if(String.valueOf(conpass.getPassword()).isEmpty()){
         conpass.setText("********");
        }
    }//GEN-LAST:event_passMousePressed

    private void conpassMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_conpassMousePressed
        if(String.valueOf(conpass.getPassword()).equals("********")){
         conpass.setText("");
        }
        
        if(username.getText().isEmpty()){
         username.setText("Ingrese nombre de usuario");
        }
         
        if(String.valueOf(pass.getPassword()).isEmpty()){
         pass.setText("********");
        }
    }//GEN-LAST:event_conpassMousePressed

